INSERT INTO `university` VALUES (1,'北京大学','北京','985',23286000,0.526,0.972,0.189,0.523,'北京大学（Peking University），简称“北大”，坐落于北京市海淀区颐和园路零五号院 ，系中华人民共和国教育部直属的全国重点大学，位列国家“双一流”A类、“985工程”、“211工程”。北京大学是中国第一所国立综合性大学，为当时中国最高教育行政机关。 创立于1898年维新变法之际，初名京师大学堂。1912年改为国立北京大学。1938年迁至昆明，更名为国立西南联合大学。1946年返回北平。1952年成为以文理基础学科为主的综合性大学，并自北京沙滩等地迁至现址。2000年与原北京医科大学合并组建为新的北京大学。北京大学是新文化运动的中心和五四运动的策源地，最早在中国传播马克思主义和科学、民主思想，是创建中国共产党的重要基地之一。北京大学为民族的振兴和解放、国家的建设和发展、社会的文明和进步做出了不可替代的贡献。'),(2,'清华大学','北京','985',NULL,NULL,NULL,NULL,NULL,NULL),(3,'复旦大学','上海','985',NULL,NULL,NULL,NULL,NULL,NULL),(4,'上海交通大学','上海','985',NULL,NULL,NULL,NULL,NULL,NULL),(5,'浙江大学','浙江','985',NULL,NULL,NULL,NULL,NULL,NULL),(6,'国防科技大学','湖南','985',NULL,NULL,NULL,NULL,NULL,NULL),(7,'中国人民大学','北京','985',NULL,NULL,NULL,NULL,NULL,NULL),(8,'南京大学','江苏','985',NULL,NULL,NULL,NULL,NULL,NULL),(9,'中国科学技术大学','安徽','985',NULL,NULL,NULL,NULL,NULL,NULL),(10,'北京航空航天大学','北京','985',NULL,NULL,NULL,NULL,NULL,NULL),(11,'同济大学','上海','985',NULL,NULL,NULL,NULL,NULL,NULL),(12,'北京理工大学','北京','985',NULL,NULL,NULL,NULL,NULL,NULL),(13,'东南大学','江苏','985',NULL,NULL,NULL,NULL,NULL,NULL),(14,'武汉大学','湖北','985',NULL,NULL,NULL,NULL,NULL,NULL),(15,'华中科技大学','湖北','985',NULL,NULL,NULL,NULL,NULL,NULL),(16,'哈尔滨工业大学','黑龙江','985',NULL,NULL,NULL,NULL,NULL,NULL),(17,'西安交通大学','陕西','985',NULL,NULL,NULL,NULL,NULL,NULL),(18,'南开大学','天津','985',NULL,NULL,NULL,NULL,NULL,NULL),(19,'北京师范大学','北京','985',NULL,NULL,NULL,NULL,NULL,NULL),(20,'华东师范大学','上海','985',NULL,NULL,NULL,NULL,NULL,NULL),(21,'电子科技大学','四川','985',21202000,0.742,0.967,0.127,0.249,'电子科技大学（University of Electronic Science and Technology of China），简称“电子科大”，坐落于四川省成都市，是中华人民共和国教育部直属高校，由教育部、工业和信息化部、四川省和成都市共建；位列国家“双一流”、“985工程”、“211工程”，是一所完整覆盖整个电子类学科，以电子信息科学技术为核心，以工为主，理工渗透，理、工、管、文、医协调发展的多科性研究型全国重点大学。学校原名成都电讯工程学院，1956年由交通大学（现上海交通大学、西安交通大学）、南京工学院（现东南大学）、华南工学院（现华南理工大学）的电讯工程有关专业合并创建而成；1960年被列为全国重点高等学校；1961年被确定为七所国防工业院校之一；1988年更名为电子科技大学；2000年由原信息产业部主管划转为教育部主管；2017年进入国家“双一流”建设高校行列。2019年教育部和四川省签约共同推进学校世界一流大学建设。'),(22,'中山大学','广东','985',NULL,NULL,NULL,NULL,NULL,NULL),(23,'天津大学','天津','985',NULL,NULL,NULL,NULL,NULL,NULL),(24,'厦门大学','福建','985',NULL,NULL,NULL,NULL,NULL,NULL),(25,'华南理工大学','广东','985',NULL,NULL,NULL,NULL,NULL,NULL),(26,'四川大学','四川','985',33059000,0.482,0.921,0.068,0.252,'四川大学（Sichuan University），简称“川大”，位于四川省成都市，是中华人民共和国教育部直属、中央直管副部级的全国重点大学；位列国家“双一流”、  “211工程”、“985工程”，是国家布局在中国西部重点建设的高水平研究型综合大学。学校由原四川大学、原成都科学技术大学、原华西医科大学三所全国重点大学经过两次合并而成。1994年原四川大学和原成都科学技术大学合并为四川联合大学，1998年恢复“四川大学”校名。2000年与原华西医科大学合并组建为新的四川大学。截至2023年1月，学校有望江、华西和江安三个校区，占地面积7050亩，校舍建筑面积282.18万平方米，在建眉山校区；有教学科研岗共6794人，全日制普通本科生3.7万余人，硕博士研究生2.9万余人。'),(27,'西北工业大学','陕西','985',NULL,NULL,NULL,NULL,NULL,NULL),(28,'山东大学','山东','985',NULL,NULL,NULL,NULL,NULL,NULL),(29,'重庆大学','重庆','985',NULL,NULL,NULL,NULL,NULL,NULL),(30,'中南大学','湖南','985',NULL,NULL,NULL,NULL,NULL,NULL),(31,'吉林大学','吉林','985',NULL,NULL,NULL,NULL,NULL,NULL),(32,'湖南大学','湖南','985',NULL,NULL,NULL,NULL,NULL,NULL),(33,'兰州大学','甘肃','985',NULL,NULL,NULL,NULL,NULL,NULL),(34,'大连理工大学','辽宁','985',NULL,NULL,NULL,NULL,NULL,NULL),(35,'中国农业大学','北京','985',NULL,NULL,NULL,NULL,NULL,NULL),(36,'东北大学','辽宁','985',NULL,NULL,NULL,NULL,NULL,NULL),(37,'中国海洋大学','山东','985',NULL,NULL,NULL,NULL,NULL,NULL),(38,'中央民族大学','北京','985',NULL,NULL,NULL,NULL,NULL,NULL),(39,'西北农林科技大学','陕西','985',NULL,NULL,NULL,NULL,NULL,NULL),(40,'郑州大学','河南','211',NULL,NULL,NULL,NULL,NULL,NULL),(41,'云南大学','云南','211',NULL,NULL,NULL,NULL,NULL,NULL),(42,'新疆大学','新疆','211',NULL,NULL,NULL,NULL,NULL,NULL),(43,'上海财经大学','上海','211',NULL,NULL,NULL,NULL,NULL,NULL),(44,'北京邮电大学','北京','211',NULL,NULL,NULL,NULL,NULL,NULL),(45,'中央财经大学','北京','211',NULL,NULL,NULL,NULL,NULL,NULL),(46,'对外经济贸易大学','北京','211',NULL,NULL,NULL,NULL,NULL,NULL),(47,'上海外国语大学','上海','211',NULL,NULL,NULL,NULL,NULL,NULL),(48,'西安电子科技大学','陕西','211',NULL,NULL,NULL,NULL,NULL,NULL),(49,'中国政法大学','北京','211',7543000,0.354,0.98,0.09,0.49,'中国政法大学（China University of Political Science and Law），简称“法大”，位于北京市，中华人民共和国教育部直属全国重点大学，由教育部和北京市人民政府共建， 是国家“双一流”建设高校、  “211工程”建设高校，是一所以法学学科为特色和优势，兼有政治学、经济学、管理学、文学、历史学、哲学、教育学、理学、工学等学科的高校，被誉为“中国法学教育的最高学府”。学校前身是1952年由北京大学、清华大学、燕京大学、辅仁大学四校的法学、政治学、社会学等学科组合而成的北京政法学院。1954年，学校迁址至学院路；1960年成为国家确定的全国重点大学；文革中学校停办，1978年复办；1983年，北京政法学院与中央政法干校合并，组建成立中国政法大学；1985年学校开辟昌平校区。'),(50,'北京外国语大学','北京','211',NULL,NULL,NULL,NULL,NULL,NULL),(51,'空军军医大学','陕西','211',NULL,NULL,NULL,NULL,NULL,NULL),(52,'北京交通大学','北京','211',NULL,NULL,NULL,NULL,NULL,NULL),(53,'南京航空航天大学','江苏','211',NULL,NULL,NULL,NULL,NULL,NULL),(54,'南京理工大学','江苏','211',NULL,NULL,NULL,NULL,NULL,NULL),(55,'上海大学','上海','211',NULL,NULL,NULL,NULL,NULL,NULL),(56,'西南财经大学','四川','211',NULL,NULL,NULL,NULL,NULL,NULL),(57,'北京科技大学','北京','211',NULL,NULL,NULL,NULL,NULL,NULL),(58,'华东理工大学','上海','211',NULL,NULL,NULL,NULL,NULL,NULL),(59,'中国传媒大学','北京','211',NULL,NULL,NULL,NULL,NULL,NULL),(60,'海军军医大学','上海','211',NULL,NULL,NULL,NULL,NULL,NULL),(61,'北京工业大学','北京','211',NULL,NULL,NULL,NULL,NULL,NULL),(62,'中南财经政法大学','湖北','211',NULL,NULL,NULL,NULL,NULL,NULL),(63,'河海大学','江苏','211',NULL,NULL,NULL,NULL,NULL,NULL),(64,'天津医科大学','天津','211',NULL,NULL,NULL,NULL,NULL,NULL),(65,'苏州大学','江苏','211',NULL,NULL,NULL,NULL,NULL,NULL),(66,'东华大学','上海','211',NULL,NULL,NULL,NULL,NULL,NULL),(67,'西南交通大学','四川','211',17240000,0.67,0.966,0.05,0.283,'西南交通大学（Southwest Jiaotong University），简称“西南交大”，位于四川省成都市，是中华人民共和国教育部直属的全国重点大学，由教育部、中国国家铁路集团有限公司、四川省人民政府和成都市人民政府共建；位列“双一流”、“211工程”、985工程优势学科创新平台，学校肇始于1896年的山海关北洋铁路官学堂，此后屡迁校址，数度更名，而以唐山交通大学扬名海内外，素有“东方康奈尔”之美誉。1952年全国高等院校院系调整，更名唐山铁道学院。1960年，经中共中央批准成为全国重点大学。1964年学校内迁峨眉，1972年定名西南交通大学。1989年学校办学主体迁成都。2000年学校划归教育部管理。'),(68,'华中师范大学','湖北','211',NULL,NULL,NULL,NULL,NULL,NULL),(69,'暨南大学','广东','211',NULL,NULL,NULL,NULL,NULL,NULL),(70,'华北电力大学','北京','211',NULL,NULL,NULL,NULL,NULL,NULL),(71,'南京师范大学','江苏','211',NULL,NULL,NULL,NULL,NULL,NULL),(72,'哈尔滨工程大学','黑龙江','211',NULL,NULL,NULL,NULL,NULL,NULL),(73,'武汉理工大学','湖北','211',NULL,NULL,NULL,NULL,NULL,NULL),(74,'陕西师范大学','陕西','211',NULL,NULL,NULL,NULL,NULL,NULL),(75,'华南师范大学','广东','211',NULL,NULL,NULL,NULL,NULL,NULL),(76,'合肥工业大学','安徽','211',NULL,NULL,NULL,NULL,NULL,NULL),(77,'北京化工大学','北京','21